// import imagePoster from './imagenes/poster/unaPeliculaDeMinecraft.webp'

import { useNavigate } from "react-router-dom";

function Categoria({ name, release_year, image, id }) {
  const navigate = useNavigate();
  const viewMovieInformation = (id_movie) => {
    navigate(`/movie/${id_movie}`, { state: {id_movie} });
  };
  return (
    <div className="relative text-xl text-white w-[300px] hover:opacity-60 hover:cursor-pointer"
    onClick={()=>viewMovieInformation(id)}>
      <img
        src={`/imagenesMovie/background/poster/${image}.webp`}

        width={300}
        height={400}
        alt=""
      />
      <div>
        <p> {name}</p>
        <p className="text-end">{release_year}</p>
      </div>
      <div className="">
        <button>Guardar</button>
      </div>
    </div>
  );
}

export default Categoria;
