// import imagePoster from './imagenes/poster/unaPeliculaDeMinecraft.webp'
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./context/AuthContext";


function Categoria({ name, release_year, image, id_movie, id_user }) {
  const [infoUser, setInfoUser] = useState([]);
  const navigate = useNavigate();
  const { user } = useAuth();
   const [movieSave, setMovieSaved]=useState({
    id_movie: id_movie,
    id_user: user?.id
  });
  console.log(user?.id)
  const viewMovieInformation = (id_movie) => {
    navigate(`/movie/${id_movie}`, { state: {id_movie} });
  };
  
  const saveMovie = (id_user, id_movie) => {

    setMovieSaved({id_movie: id_movie, id_user: id_user})
      console.log(movieSave)
  }

  return (
    <div className="relative text-xl text-white w-[300px] "
    >
      <img
        src={`/imagenesMovie/background/poster/${image}.webp`}
        className="hover:opacity-60 hover:cursor-pointer"
        width={300}
        height={400}
        onClick={()=>viewMovieInformation(id_movie)}
        alt=""
      />
      <div>
        <p> {name}</p>
        <p className="text-end">{release_year}</p>
      </div>
      <div className="absolute flex top-1 right-1">
        <div className="bg-red-600 px-2 rounded " onClick={()=>saveMovie(user?.id, id_movie )}>Guardar</div>
        <div className="bg-blue-500 px-2 rounded">Eliminar</div>
      </div>
    </div>
  );
}

export default Categoria;
