import { useEffect, useState } from "react";
import { useAuth } from "./context/AuthContext";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Categoria from "./Categoria";
import PanelMovie from "./PanelMovie";
function Home() {
  const [movies, setMovies] = useState([]);
  const [genre, setGenres] = useState([]);
  const [movie_genre, setMovieGenres] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8081/movies").then((res) => {
      setMovies(res.data);
    });
    axios.get("http://localhost:8081/genre").then((res) => {
      setGenres(res.data);
    });
    axios.get("http://localhost:8081/movie_genrs").then((res) => {
      setMovieGenres(res.data);
    });
  }, []);

  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const handleLogOut = () => {
    logout();
    navigate("/");
  };

  return (
    <div className="relative min-h-screen bg-black text-white">
      <header className="fixed top-0 h-[90px] w-full bg-black/40">
        <nav className="h-full">
          <div className="flex text-red-600 font-bold items-center justify-center h-full">
            <div className="flex items-center justify-between w-full px-28">
              <span className="text-3xl">PELISUNCP</span>
              <div className="flex gap-4 items-center text-white">
                <span>Bienvenido, {user?.name}</span>
                <span>
                  <button
                    className="bg-red-600 text-white px-6 py-1 rounded"
                    onClick={handleLogOut}
                  >
                    SALIR
                  </button>
                </span>
              </div>
            </div>
          </div>
        </nav>
      </header>
      {/* <h1>{user?.name}</h1>
      <p>{user?.role}</p>
      {user?.role === "administrador" && (
        <button className="absolute top-0 right-0 px-4 py-2 rounded bg-blue-500">
          Añadir película
        </button>
      )} */}

      {/* <div>{movies[0]?.title}</div>
      <div>{movies[0]?.id}</div>
      <div>{movies[0]?.description}</div> */}
      <div className="pt-[90px] text-white">
        <div className="flex items-center justify-center">
          {/* style={{backgroundImage: url(`./imagenes/background/${movies?.[0].background_url}.webp`)}} */}
          
          <PanelMovie/>
        </div>
      </div>

      <div className="pt-20">
        <h3 className="text-center font-bold text-2xl">CATEGORIAS</h3>
        <div className="px-28 py-8">
          {genre?.map((g) => {
            // Encuentra todos los id_movie que pertenecen a este género
            const movieIds = movie_genre
              .filter((mg) => mg.id_genre === g.id)
              .map((mg) => mg.id_movie);

            // Filtra las películas cuyo id esté en movieIds
            const filteredMovies = movies.filter((m) =>
              movieIds.includes(m.id)
            );

            return (
              <div key={g.id}>
                <p className="text-3xl font-semibold my-4 text-red-600">
                  {g.name}
                </p>
                <div className="flex gap-8 overflow-x-auto">
                  {filteredMovies.map((movie) => (
                    <div key={movie.id}>
                      <Categoria
                        id={movie.id}
                        name={movie.title}
                        release_year={new Date(movie.release_year)
                          .toISOString()
                          .slice(0, 10)}
                        image={movie.photo_url}
                      />
                    </div>
                  ))}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Home;
