import { useEffect, useState } from "react";
import { useAuth } from "./context/AuthContext";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Categoria from "./Categoria";
function Home() {
  const [movies, setMovies] = useState([]);
  const [genre, setGenres] = useState([]);
  const [movie_genre, setMovieGenres] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8081/movies").then((res) => {
      setMovies(res.data);
    });
    axios
      .get("http://localhost:8081/genre").then((res)=>{
        setGenres(res.data)
      });
    axios
      .get("http://localhost:8081/movie_genrs").then((res)=>{
        setMovieGenres(res.data)
      });
  }, []);

  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const handleLogOut = () => {
    logout();
    navigate("/");
  };
  return (
    <div className="relative min-h-screen bg-black text-white">
      <header className="fixed top-0 h-[90px] w-full bg-black/40">
        <nav className="h-full">
          <div className="flex text-red-600 font-bold items-center justify-center h-full">
            <div className="flex items-center justify-between w-full px-28">
              <span className="text-3xl">PELISUNCP</span>
            <div className="flex gap-4 items-center text-white">
              <span>Bienvenido, {user?.name}</span>
              <span>
                <button
                  className="bg-red-600 text-white px-6 py-1 rounded"
                  onClick={handleLogOut}
                >
                  SALIR
                </button>
              </span>
            </div>
            </div>
          </div>
        </nav>
      </header>
      {/* <h1>{user?.name}</h1>
      <p>{user?.role}</p>
      {user?.role === "administrador" && (
        <button className="absolute top-0 right-0 px-4 py-2 rounded bg-blue-500">
          Añadir película
        </button>
      )} */}
     
      {/* <div>{movies[0]?.title}</div>
      <div>{movies[0]?.id}</div>
      <div>{movies[0]?.description}</div> */}
       <div className="pt-[90px] text-white">
        <div className="flex items-center justify-center">
          <div className="bg-green-200 w-[1300px] h-[540px] flex items-center">
            <div className="px-10 py-20 flex gap-20  w-full">
              <img width={350} height={400} loading="lazy" alt="" className="min-h-[400px] min-w-[350px] max-h-[400px] max-w-[350px]"/>
              <div className="flex flex-col gap-2 w-full">
                <h2 className="text-4xl font-bold">Una Pelicula de minecraft</h2>
                <p className="text-xs">2025-03-31</p>
                <p className="break-words max-w-[500px] text-justify ">Cuatro inadaptados se encuentran luchando con problemas ordinarios cuando de repente se ven arrastrados a través de un misterioso portal al Mundo Exterior: un extraño país de las maravillas cúbico que se nutre de la imaginación. Para volver a casa, tendrán que dominar este mundo mientras se embarcan en una búsqueda mágica con un inesperado experto artesano, Steve.</p>
                <div>Géneros</div>
                <div className="flex gap-4">
                  <span className="bg-red-500 px-3 py-1 rounded-lg">ACCION</span>
                  <span className="bg-red-500 px-3 py-1 rounded-lg">AVENTURA</span>
                  <span className="bg-red-500 px-3 py-1 rounded-lg">FICCIÓN</span>
                </div>
                <div>
                 Compañias de producción
                </div>
                 <div className="flex gap-3 text-blue-600 font-bold">
                  <span>Warret Bros Pictures</span>
                  <span>Legendary Pictures</span>
                  <span>Legensdsd</span>
                </div>
                <div className="flex gap-10 justify-center w-full font-bold pt-5">
                  <button className="bg-white/60 text-blue-600 px-3 py-2 rounded-md">VER TRAILER</button>
                  <button className="bg-red-600/85 px-3 py-2 rounded-md">VER INFORMACIÓN</button>
                </div>
              </div>
              
            </div>
          </div>
        </div>
        
      </div>
      <div className="pt-20">
  <h3 className="text-center font-bold text-2xl">CATEGORIAS</h3>

  {genre?.map((g) => {
  // Encuentra todos los id_movie que pertenecen a este género
  const movieIds = movie_genre
    .filter((mg) => mg.id_genre === g.id)
    .map((mg) => mg.id_movie);

  // Filtra las películas cuyo id esté en movieIds
  const filteredMovies = movies.filter((m) => movieIds.includes(m.id));

  return (
    <div key={g.id}>
      <p className="text-xl font-semibold mt-4">{g.name}</p>
      <div className="flex">
      {filteredMovies.map((movie) => (
        <div key={movie.id}>
          <Categoria name={movie.title} release_year={new Date(movie.release_year).toISOString().slice(0, 10)} image={movie.photo_url}/>
        </div>
      ))}
      </div>
    </div>
  );
})}


</div>

    </div>
  );
}

export default Home;
