import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Nav from "../NavComponent/Nav";
import axios from "axios";

function Favoritos() {
  const location = useLocation();
  const id_persona = location?.state.id;
  const [movies, setMovies] = useState([]);
  useEffect(()=>{
    axios
    .get(`http://localhost:8081/favorite-saved-user?id_user=${id_persona}`)
    .then((res)=>{
      setMovies(res.data);
    })
    .catch((err)=> console.log("Error: ", err))
  },[id_persona])

  return (
    <div className="bg-black min-h-screen text-white">
      <Nav />
      <div className="pt-[90px] grid ">
        <div className="mt-10 py-2 text-center font-bold text-2xl bg-gradient-to-r from-black via-red-600 to-black">
          FAVORITOS
        </div>

        <div className="grid grid-cols-2 max-w-[1200px] bg-white justify-items-center mt-8 text-center">
          {movies.map((element)=>
          <div className="w-full" key={element.id}>
            <div className={`flex gap-2 px-4 py-4 justify-end`}>
              <img
              src={`/imagenesMovie/background/poster/${element.photo_url}.webp`}
                width={200}
                height={300}
                className="min-h-[300px] min-w-[200px]"
                alt=""
              />
              <div className="w-1/2">
                <p>{element.title}</p>
                <p className="text-start">{new Date(element.release_year).toISOString()
                        .slice(0, 10)}</p>
                <p className="text-justify ">
                  {element.description}
                </p>
                <div className="flex gap-2 justify-end">
                  <button
                    type="button"
                    className="bg-red-600  h-[30px] px-2 text-white"
                  >
                    Eliminar
                  </button>
                  <button
                    type="button"
                    className="bg-white px-2 h-[30px] text-black"
                  >
                    Ver informaci√≥n
                  </button>
                </div>
              </div>
            </div>
          </div>

          )}
        </div>
      </div>
      Favoritos {id_persona}
    </div>
  );
}

export default Favoritos;
